eft: 0;background-size: cover;}.cl-template--id-153 .cl-element--instance-1004 .cl-element-featured_media__placeholder-image {fill: #c2c2c2;}/* section */.cl-template--id-153 div.cl-element--instance-1005{text-align: left;font-weight:normal;font-style: normal;text-decoration: none;margin: 0px;padding: 10px 0px 0px 0px;border-radius: 0px;justify-content: flex-start;}'),
