(31, 1, '2023-03-28 05:58:39', '2023-03-28 05:58:39', '<!-- wp:paragraph -->\n<p>KONTAK</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Silakan menghubungi kami untuk informasi lebih lanjut</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/alamat.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>ALAMAT</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Jl. Diponegoro No.186, Ngablak, Gedanganak, Kec. Ungaran Tim., Kabupaten Semarang, Jawa Tengah 50512</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/telp.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>TELEPHONE</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Tel: (021) 5221224 Fax: (021) 5203873</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/peta.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>LOKASI</h5>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3958.781657398069!2d110.40420667036469!3d-7.151226154334725!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2e70861edd5fad7b%3A0x87b6e86cfcc66e3c!2sUniversitas%20Ngudi%20Waluyo!5e0!3m2!1sid!2sid!4v1683966584363!5m2!1sid!2sid\" width=\"100%\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>\n<!-- /wp:html -->', 'Hubungi Kami', '', 'publish', 'closed', 'closed', '', 'hubungi-kami', '', '', '2023-05-13 08:30:59', '2023-05-13 08:30:59', '', 0, 'http://localhost/profilesekolah/?page_id=31', 0, 'page', '', 0),
