"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-2.png\" alt=\"\" class=\"wp-image-1609\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1610} -->\n<figure class=\"wp-block-image\"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-3.png\" alt=\"\" class=\"wp-image-1610\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1611} -->\n<figure class=\"wp-block-image\"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-4.png\" alt=\"\" class=\"wp-image-1611\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1612} -->\n<figure class=\"wp-block-image\"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-5.png\" alt=\"\" class=\"wp-image-1612\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1614} -->\n<figure class=\"wp-block-image\"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-7.png\" alt=\"\" class=\"wp-image-1614\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1615} -->\n<figure class=\"wp-block-image\"><img src=\"https://sman1ngluwar.sch.id/wp-content/uploads/2023/02/siaga-8.png\" alt=\"\" class=\"wp-image-1615\"/></figure>\n<!-- /wp:image -->', 'PESTA SIAGA 2023 DI SMAN 1 NGLUWAR', '', 'inherit', 'closed', 'closed', '', '80-revision-v1', '', '', '2023-03-28 07:50:20', '2023-03-28 07:50:20', '', 80, 'http://localhost/profilesekolah/?p=82', 0, 'revision', '', 0);
