 pengembangan kajian keagamaan, sain teknologi, seni dan budaya.<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li></li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list --></li>\n<!-- /wp:list-item --></ol>\n<!-- /wp:list -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->', 'Visi Misi', '', 'inherit', 'closed', 'closed', '', '14-revision-v1', '', '', '2023-03-28 10:00:42', '2023-03-28 10:00:42', '', 14, 'http://localhost/profilesekolah/?p=141', 0, 'revision', '', 0),
