(320, 1, '2023-05-13 08:30:07', '2023-05-13 08:30:07', '<!-- wp:paragraph -->\n<p>KONTAK</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Silakan menghubungi kami untuk informasi lebih lanjut</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/alamat.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>ALAMAT</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Direktorat Promosi Kesehatan dan Pemberdayaan Masyarakat Kementerian Kesehatan RI<br>Gedung dr. Adhyatma Lt. 6, Jl. H.R. Rasuna Said Blok X5<br>Kav. 4-9 Jakarta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/telp.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>TELEPHONE</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Tel: (021) 5221224 Fax: (021) 5203873</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><img src=\"https://promkes.kemkes.go.id/__asset/__images/kontak/peta.png\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5>LOKASI</h5>\n<!-- /wp:heading -->', 'Hubungi Kami', '', 'inherit', 'closed', 'closed', '', '31-autosave-v1', '', '', '2023-05-13 08:30:07', '2023-05-13 08:30:07', '', 31, 'http://localhost/kesehatan/?p=320', 0, 'revision', '', 0),
